class MyThread(QtCore.QThread):
    update_signal = pyqtSignal(int)

    def __init__(self, parent=None):
        QtCore.QThread.__init__(self, parent)

    def run(self):
        while not main.stop:
            main.add()
            self.update_signal.emit(0)
            self.sleep(1)


